import React,{useState} from 'react'
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';

const CreateNote = () => {

    const[note,setNote]=useState({
        title:'',
        content:'',
    });
    const InputEvent=(event)=>{
        
        const value=event.target.value;

        setNote((prev)=>{
            return {
                ...prev,
            }
        })
    }



    return (
        <div>
            <form>
            <input type="text" name="title" value={note.title} onChange={InputEvent} placeholder='Title' autoComplete="off"></input>
            <textarea rows=' ' name="content" value= {note.content} onChange={InputEvent} column="" placeholder="Write a note..." />
<Button>
<AddIcon />

</Button>
            </form>
        </div>
    )
}

export default CreateNote
