import React,{useState} from 'react';
import Title from './images/comps/Title';
import UploadForm from './images/comps/UploadForm';
import ImageGrid from './images/comps/ImageGrid';
import Modal from './images/comps/Modal';

function App() {

    const [selectedImg, setSelectedImg] = useState(null);

  return (
    <div className="App">
      <Title/>
      <UploadForm />
      <ImageGrid setSelectedImg={setSelectedImg} />
      { selectedImg && (
      <Modal selectedImg={selectedImg} setSelectedImg={setSelectedImg} />
      )}
    </div>
  );
}

export default App;
