import React,{useState} from 'react'
import Header from './Header'
import CreateNote from './CreateNote'
import Note from './Note'

const App2 = () => {
     
    const[addItem,setAddItem]=useState([]);




    const addNote=(note)=>{
       // alert("i am clicked");


        setAddItem((prev)=>{
            return [...prev,note]
        });

    }

   const onDelete=(id)=>{
             setAddItem((old)=>
                 old.filter((currdata,indx)=>{
                      return indx!==id;
                 })
             )
   }


    return (
        <div>
            <Header />
            <CreateNote 
            passNote={addNote}
            />
          

            {addItem.map((val,index)=> {
                  return    <Note 
                  key={index}
                  id={index}
                  title={val.title}
                 content={val.content}
                 deleteItem={onDelete}
                  />
            })}
        </div>
    )
}

export default App2;
