import React,{useState} from 'react'
import Header from './Header'
import CreateNote from './CreateNote'
import Note from './Note'

const App2 = () => {
     
    const[addItem,setAddItem]=useState([]);




    const addNote=(note)=>{
        alert("i am clicked");


        setAddItem((prev)=>{
            return [...prev,note]
        });
        
    }

   


    return (
        <div>
            <Header />
            <CreateNote 
            passNote={addNote}
            />
            <Note />
        </div>
    )
}

export default App2;
